mapper.select:
select A.*,B.batch_num,B.seq_date from tb_sample A,tb_batch B where A.batch_id=B.id
 AND A.batch_id= #{batchId} 
 AND A.is_supplement= #{isSupplement} 
 AND B.batch_num= #{batchNum} 
and A.unit_from like CONCAT('%',#{unitFrom},'%')
 and A.sample_id   like CONCAT('%',#{sampleId},'%') 
 and A.name like CONCAT('%',#{name},'%')
  and A.report_state = #{reportState}

	 select * from tb_sample where batch_id= #{batchId}
	   AND batch_id= #{batchId}
	   AND DOWNLOAD_STATUS= #{downloadStatus}
	   AND audit_status= #{auditStatus}
	  ${sql}
	   and sample_id = #{sampleId}
	   and res13 = #{tbRes13}
       and res18 = #{tbRes18}
       and res21 = #{tbRes21}
	   and name like CONCAT('%',#{name},'%')
	   and unit_from like CONCAT('%',#{unitFrom},'%')

	SELECT	s.*,CASE s.tips WHEN 0 THEN -1 WHEN 4 THEN 	-2 ELSE  s.tips END a
	FROM  tb_sample s WHERE s.tips is not null and s.status=2
	   AND s.batch_id= #{batchId}
	   AND s.DOWNLOAD_STATUS= #{downloadStatus}
	   AND s.audit_status= #{auditStatus}
	  ${sql}
	   and s.sample_id = #{sampleId}
	   and s.res13 = #{tbRes13}
       and s.res18 = #{tbRes18}
       and s.res21 = #{tbRes21}
	   and s.name like CONCAT('%',#{name},'%')
	   and unit_from like CONCAT('%',#{unitFrom},'%')

	ORDER BY a asc,sample_id ASC

	SELECT	s.*,CASE s.tips WHEN 0 THEN -1 WHEN 4 THEN 	-2 ELSE  s.tips END a
	FROM  tb_sample s WHERE s.tips is not null and s.status=2
	   AND s.batch_id= #{batchId}
	   AND s.DOWNLOAD_STATUS= #{downloadStatus}
	   AND s.audit_status= #{auditStatus}
	  ${sql}
	   and s.sample_id = #{sampleId}
	   and s.res13 = #{tbRes13}
       and s.res18 = #{tbRes18}
       and s.res21 = #{tbRes21}
	   and s.name like CONCAT('%',#{name},'%')
	   and unit_from like CONCAT('%',#{unitFrom},'%')

	ORDER BY a asc,sample_id ASC

   SELECT * from tb_sample WHERE 1=1
   AND batch_id= #{batchId}
  AND DOWNLOAD_STATUS= #{downloadStatus}
   AND audit_status= #{auditStatus}
  ${sql}
   and unit_from like CONCAT('%',#{unitFrom},'%')

		and sample_id like CONCAT('%',#{sampleId},'%')


	 	and recieve_date between #{sRecieveDate} and #{eRecieveDate}  


	 	and recieve_date < #{eRecieveDate} 


	 	and recieve_date > #{sRecieveDate} 


	 	and report_date between #{sReportDate} and #{eReportDate}  


	 	and report_date < #{eReportDate} 


	 	and report_date > #{sReportDate} 

  order by tips DESC

mapper.update:
UPDATE tb_sample SET AUDIT_STATUS= '1' 

	, report_date=#{reportDate,jdbcType=VARCHAR}

WHERE id = #{tbSampleId}

UPDATE tb_sample SET SUPPLEMENT_STATUS= '1' 

	, supplement_date=#{supplementDate,jdbcType=VARCHAR}

WHERE id = #{tbSampleId}

UPDATE tb_sample SET tips=#{tips} ,tips_state =#{tipsState}

	,report_state = 0


	,report_state = 1

WHERE id = #{tbSampleId}

UPDATE tb_sample SET DOWNLOAD_STATUS= '1' 

	,report_template = #{reportTemplate}

WHERE id = #{tbSampleId}

